import React from 'react';
import './LoginPage.css';
import './index.css';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import  { useState } from 'react';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const navigateToSignUpPage = () => {
    navigate('/SignUpPage');
  };

  const navigateToResetPage = () => {
    navigate('/ResetPassword');
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = () => {
    // 예제: 간단한 인증 로직 (실제 사용에서는 보안을 강화해야 합니다.)
    if (email === '1' && password === '1') {
      // 아이디와 비밀번호가 일치할 경우 Home 페이지로 이동
      navigate('/home');
    } else {
      // 인증 실패 시 처리
      alert('Invalid email or password');
    }
  };

  return (
    <div className='login'>
      <div className='title'>Log In</div>
      <BackButton></BackButton>

      <div className='input-button-group'>
        <input
          type='email'
          value={email}
          onChange={handleEmailChange}
          className='input-button-group'
          placeholder='email'
        />
      </div>

      <div className='input-button-group'>
        <input
          type='password'
          value={password}
          onChange={handlePasswordChange}
          className='input-button-group'
          placeholder='password'
        />
      </div>

      <div className='black-button-group' style={{ marginTop: '20px' }}>
        <button onClick={handleLogin}>LOG IN</button>
      </div>

      <div className='grey-button-group'>
        <button onClick={navigateToSignUpPage}>Sign Up</button>
      </div>

      <div className='smalltext'>
        <p onClick={navigateToResetPage}>Forgot password?</p>
      </div>
    </div>
  );
}

export default LoginPage;
