import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import LoginPage from './LoginPage';

function App() {
  // useNavigate를 사용하기 위해 라우터 컨텍스트 내부에서 렌더링됩니다.
  const navigate = useNavigate();

  const navigateToLoginPage = () => {
    // useNavigate를 통해 "/LoginPage"로 이동합니다.
    navigate("/LoginPage");
  };

  return (
    <Router>
      {/* <Routes>로 전체 애플리케이션의 라우팅 구조를 설정합니다. */}
      <Routes>
        {/* "/" 경로에 대한 라우팅 설정 */}
        <Route path="/" element={<Home />} />

        {/* "/LoginPage" 경로에 대한 라우팅 설정 */}
        <Route path="/LoginPage" element={<LoginPage />} />
      </Routes>
    </Router>
  );
}

function Home() {
  // "/" 경로에 대한 컴포넌트입니다.
  return (
    <div className="App">
      <div className="centered-title">
        <p id="logo-text">moodify</p>
      </div>
      <div className="button-group">
        {/* 버튼 클릭 시 navigateToLoginPage 함수를 통해 "/LoginPage"로 이동합니다. */}
        <button onClick={ navigateToLoginPage }>START</button>
      </div>
      <div>
        <img src="/images/moodifymainpic.png" alt="메인화면사진" className="main-image" />
      </div>
    </div>
  );
}

export default Home;
