import React, { useState } from 'react';

const Calendar = () => {
  const [emoji, setEmoji] = useState('');
  const [date, setDate] = useState('');
  const [diary, setDiary] = useState('');
  const [entries, setEntries] = useState(
    JSON.parse(localStorage.getItem('emojiCalendar')) || {}
  );

  const handleAddEntry = () => {
    if (emoji && date && diary) {
      const newEntries = {
        ...entries,
        [date]: { emoji, diary }
      };
      setEntries(newEntries);
      localStorage.setItem('emojiCalendar', JSON.stringify(newEntries));
      setEmoji('');
      setDate('');
      setDiary('');
    } else {
      alert('모든 필드를 채워주세요.');
    }
  };

  return (
    <div>
      <h2>감정 이모티콘 달력</h2>
      <button onClick={() => setEntries({})}>모든 데이터 삭제</button>
      <div>
        <label>날짜:</label>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        <label>감정 이모티콘:</label>
        <input type="text" value={emoji} onChange={(e) => setEmoji(e.target.value)} />
        <label>일기:</label>
        <textarea value={diary} onChange={(e) => setDiary(e.target.value)} />
        <button onClick={handleAddEntry}>추가</button>
      </div>
      <div>
        {Object.entries(entries).map(([date, { emoji, diary }]) => (
          <div key={date}>
            <h3>{date}</h3>
            <p>{emoji}</p>
            <p>{diary}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendar;
