// DiaryModal.js
import React, { useState } from 'react';
const DiaryModal = ({ onAdd, onEdit, onDelete, onClose, selectedEmoji, index }) => {
const [diaryText, setDiaryText] = useState('');
const [selectedDate, setSelectedDate] = useState('');
const handleSubmit = () => {
if (index !== undefined) {
onEdit(diaryText, selectedDate, index);
} else {
onAdd(diaryText, selectedDate);
}
};
const handleDelete = () => {
onDelete(index);
};
return (
<div>
<p>Selected Emoji: {selectedEmoji}</p>
<label>Date:</label>
<input type="date" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)} />
<textarea value={diaryText} onChange={(e) => setDiaryText(e.target.value)} />
<button onClick={handleSubmit}>{index !== undefined ? 'Edit' : 'Add'}</button>
{index !== undefined && <button onClick={handleDelete}>Delete</button>}
</div>
);
};
export default DiaryModal;
