    import React, { useState } from 'react';

    const DiaryModal = ({ onAdd, onClose, selectedEmoji, selectedDate }) => {
      const [diaryText, setDiaryText] = useState('');
    
      const handleSubmit = () => {
        onAdd(diaryText, selectedDate); // 일기 내용과 선택된 날짜를 전달합니다.
        onClose();
      };
    
      return (
        <div>
          <p>Selected Emoji: {selectedEmoji}</p>
          <p>Date: {selectedDate}</p> {/* 선택된 날짜 표시 */}
          <textarea value={diaryText} onChange={(e) => setDiaryText(e.target.value)} />
          <button onClick={handleSubmit}>Save</button>
