import React, { useState } from 'react';
import BackButton from './BackButton';

const Findfriends = () => {
  const allFriends = [
    { id: 1, name: 'John Doe' },
    { id: 2, name: 'Jane Doe' },
    // 필요한 만큼 여기에 더 추가하실 수 있습니다.
  ];

  const [friendList, setFriendList] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResult, setSearchResult] = useState([]);

  const handleSearch = () => {
    const result = allFriends.filter(friend =>
      friend.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setSearchResult(result);
  };

  const handleAddFriend = (friend) => {
    if (!friendList.find(f => f.id === friend.id)) {
      setFriendList([...friendList, friend]);
    }
    setSearchResult([]);
    setSearchQuery('');
  };

  return (
    <div>
      <div className='title'>Friend List</div>
      <BackButton></BackButton>
      <ul>
        {friendList.map(friend => (
          <li key={friend.id}>{friend.name}</li>
        ))}
      </ul>

      <h2>친구 검색</h2>
      <input
        type="text"
        value={searchQuery}
        onChange={event => setSearchQuery(event.target.value)}
      />
      <button onClick={handleSearch}>검색</button>

      <h2>검색 결과</h2>
      <ul>
        {searchResult.map(friend => (
          <li key={friend.id}>
            {friend.name}
            <button onClick={() => handleAddFriend(friend)}>추가</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Findfriends;
