// HomeComponent.js

import React, { useState } from 'react';
import { useUser } from './UserContext';
import CalendarModal from './CalendarModal';
import EmojiModal from './EmojiModal';
import DiaryModal from './DiaryModal';

const HomeComponent = () => {
  const { user, addDiaryEntry, updateUser } = useUser();
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedEmoji, setSelectedEmoji] = useState(null);
  const [showCalendarModal, setShowCalendarModal] = useState(false);
  const [showEmojiModal, setShowEmojiModal] = useState(false);
  const [showDiaryModal, setShowDiaryModal] = useState(false);

  const handleEmojiSelect = (emoji) => {
    setSelectedEmoji(emoji);
    setShowEmojiModal(false);
    setShowDiaryModal(true);
  };

  const handleDiarySave = (diaryEntry) => {
    addDiaryEntry(selectedDate, selectedEmoji, diaryEntry);
    setSelectedDate(null);
    setSelectedEmoji(null);
    setShowDiaryModal(false);
  };

  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setShowCalendarModal(false);
    setShowEmojiModal(true);
  };

  const handleOpenEmojiModal = () => {
    setShowEmojiModal(true);
  };

  // 기존의 handleYearMonthSelect, updateUserCalendarData 등의 함수는 그대로 유지

  return (
    <div>
      {/* 년도 및 달 선택 모달 */}
      {showCalendarModal && (
        <CalendarModal onClose={() => setShowCalendarModal(false)} onDateSelect={handleDateSelect} />
      )}

      {/* 이모티콘 선택 모달 */}
      {showEmojiModal && (
        <EmojiModal onClose={() => setShowEmojiModal(false)} onEmojiSelect={handleEmojiSelect} />
      )}

      {/* 일기 작성 모달 */}
      {showDiaryModal && (
        <DiaryModal onClose={() => setShowDiaryModal(false)} onDiarySave={handleDiarySave} selectedEmoji={selectedEmoji} />
      )}

      {/* 모달 표시 버튼 */}
      <button onClick={() => setShowCalendarModal(true)}>Select Year and Month</button>
      <button onClick={handleOpenEmojiModal}>+</button>

      {/* 기존의 렌더링 로직은 그대로 유지 */}
    </div>
  );
};

export default HomeComponent;
