// DiaryModal.js
import React, { useState } from 'react';
import CalendarModal from './CalendarModal';

const DiaryModal = ({ onAdd, onClose, selectedEmoji }) => {
  const [selectedDate, setSelectedDate] = useState('');
  const [diaryText, setDiaryText] = useState('');

  const handleSaveDiary = () => {
    onAdd(diaryText, selectedDate);
    onClose();
  };

  return (
    <div>
      <button onClick={() => setSelectedDate('')}>
        Select Date
      </button>
      <p>Selected Emoji: {selectedEmoji}</p>
      <p>Selected Date: {selectedDate}</p>
      <textarea value={diaryText} onChange={(e) => setDiaryText(e.target.value)} />
      <button onClick={handleSaveDiary}>Save</button>
      {selectedDate && <CalendarModal onSelect={(date) => setSelectedDate(date)} onClose={() => setSelectedDate('')} />}
    </div>
  );
};

export default DiaryModal;
