// DiaryModal.js
import React, { useState } from 'react';

const DiaryModal = ({ onAdd, onClose, selectedEmoji, selectedDate }) => {
  const [diaryText, setDiaryText] = useState('');
  const [date, setDate] = useState(selectedDate); // selectedDate를 초기 날짜로 설정

  const handleSubmit = () => {
    onAdd(diaryText, date);
    onClose();
  };

  return (
    <div>
      <p>Selected Emoji: {selectedEmoji}</p>
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      <textarea value={diaryText} onChange={(e) => setDiaryText(e.target.value)} />
      <button onClick={handleSubmit}>Save</button>
    </div>
  );
};

export default DiaryModal;
