    import React, { useState, useEffect } from 'react';

    const DiaryModal = ({ onAdd, onClose, selectedEmoji, selectedDate, diaryEntries = [] }) => {
      const [diaryText, setDiaryText] = useState('');
    
      // 선택된 날짜에 해당하는 일기 내용을 찾아 diaryText 상태에 설정합니다.
      useEffect(() => {
        const selectedDiary = diaryEntries.find(entry => entry.date === selectedDate);
        if (selectedDiary) {
          setDiaryText(selectedDiary.text);
        } else {
          setDiaryText('');
        }
      }, [selectedDate, diaryEntries]);
    
      const handleSubmit = () => {
        onAdd(diaryText, selectedDate);
        onClose();
      };
    
      return (
        <div>
          <p>Selected Emoji: {selectedEmoji}</p>
          <p>Date: {selectedDate}</p>
          <textarea value={diaryText} onChange={(e) => setDiaryText(e.target.value)} />
          <button onClick={handleSubmit}>Save</button>
        </div>
      );
    };
    
    export default DiaryModal;
