// DiaryModal.js
import React, { useState, useEffect } from 'react';

const DiaryModal = ({ onAdd, onEdit, onDelete, onClose, selectedEmoji, selectedText, selectedIndex, selectedDate: initialDate }) => {
  const [diaryText, setDiaryText] = useState(selectedText || '');
  const [selectedDate, setSelectedDate] = useState(initialDate || '');

  useEffect(() => {
    setDiaryText(selectedText || '');
    setSelectedDate(initialDate || '');
  }, [selectedText, initialDate]);

  const handleSubmit = () => {
    if (selectedIndex !== null) {
      onEdit(diaryText, selectedDate, selectedIndex);
    } else {
      onAdd(diaryText, selectedDate);
    }
  };

  const handleDelete = () => {
    onDelete(selectedIndex);
  };

return (
  <div>
    <p>Selected Emoji: {selectedEmoji}</p>
    {selectedIndex !== null ? (
      <>
        <p>Date: {selectedDate}</p>
        <p>{diaryText}</p>
        <button onClick={handleDelete}>Delete</button>
      </>
    ) : (
      <>
        <label>Date:</label>
        <input type="date" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)} />
        <textarea value={diaryText} onChange={(e) => setDiaryText(e.target.value)} />
        <button onClick={handleSubmit}>Add</button>
      </>
    )}
  </div>
);
};

export default DiaryModal;
