import React, { useState, useEffect } from 'react';

const CalendarModal = ({ onClose, onYearMonthSelect }) => {
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedMonth, setSelectedMonth] = useState('');
  const [yearList, setYearList] = useState([]);
  const [monthList, setMonthList] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [emoji, setEmoji] = useState('');
  const [diaryEntry, setDiaryEntry] = useState('');
  const [diaryEntries, setDiaryEntries] = useState({});

  useEffect(() => {
    // 년도 목록 생성 (예: 2020부터 2030까지)
    const startYear = 2020;
    const years = Array.from({ length: 11 }, (_, index) => startYear + index);
    setYearList(years);

    // 달 목록 생성 (1부터 12까지)
    const months = Array.from({ length: 12 }, (_, index) => index + 1);
    setMonthList(months);
  }, []);

  const daysInMonth = (year, month) => new Date(year, month, 0).getDate();
  const getFirstDayOfMonth = (year, month) => new Date(year, month - 1, 1).getDay();

  const handleSelectYearMonth = () => {
    if (selectedYear && selectedMonth) {
      onYearMonthSelect(selectedYear, selectedMonth);
    } else {
      alert('Please select both year and month.');
    }
  };

  const handleDateClick = (day) => {
    const selectedDateStr = `${selectedYear}-${selectedMonth}-${day}`;
    setSelectedDate(selectedDateStr);
    setEmoji('');
    setDiaryEntry('');
  };

  const handleSaveEntry = () => {
    if (selectedDate) {
      setDiaryEntries({
        ...diaryEntries,
        [selectedDate]: { emoji, diaryEntry },
      });
      setSelectedDate(null);
      setEmoji('');
      setDiaryEntry('');
    }
  };

  return (
    <div>
      <h3>Select Year and Month</h3>
      <label>Year:</label>
      <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
        <option value="">Select Year</option>
        {yearList.map((year) => (
          <option key={year} value={year}>
            {year}
          </option>
        ))}
      </select>
      <label>Month:</label>
      <select value={selectedMonth} onChange={(e) => setSelectedMonth(e.target.value)}>
        <option value="">Select Month</option>
        {monthList.map((month) => (
          <option key={month} value={month}>
            {month}
          </option>
        ))}
      </select>
      <button onClick={handleSelectYearMonth}>Select</button>

      {selectedYear && selectedMonth && (
        <div className="calendar">
          <table>
            <thead>
              <tr>
                <th>Sun</th>
                <th>Mon</th>
                <th>Tue</th>
                <th>Wed</th>
                <th>Thu</th>
                <th>Fri</th>
                <th>Sat</th>
              </tr>
            </thead>
            <tbody>
              {Array.from({ length: daysInMonth(selectedYear, selectedMonth) }, (_, index) => (
                <tr key={index}>
                  {Array.from({ length: 7 }, (_, dayIndex) => {
                    const dayNumber = index * 7 + dayIndex - getFirstDayOfMonth(selectedYear, selectedMonth) + 2;
                    const isSelected = selectedDate === `${selectedYear}-${selectedMonth}-${dayNumber}`;
                    const entry = diaryEntries[`${selectedYear}-${selectedMonth}-${dayNumber}`];

                    return (
                      <td
                        key={dayIndex}
                        className={dayNumber <= 0 ? 'empty' : isSelected ? 'selected' : ''}
                        onClick={() => handleDateClick(dayNumber)}
                      >
                        {dayNumber > 0 && dayNumber <= daysInMonth(selectedYear, selectedMonth) && (
                          <div className="day-container">
                            <span className="day">{dayNumber}</span>
                            {entry && (
                              <div className="entry">
                                <span className="emoji">{entry.emoji}</span>
                                <span className="diary">{entry.diaryEntry}</span>
                              </div>
                            )}
                          </div>
                        )}
                      </td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {selectedDate && (
        <div className="entry-form">
          <label>Emoji:</label>
          <input type="text" value={emoji} onChange={(e) => setEmoji(e.target.value)} />
          <label>Diary Entry:</label>
          <textarea value={diaryEntry} onChange={(e) => setDiaryEntry(e.target.value)} />
          <button onClick={handleSaveEntry}>Save Entry</button>
        </div>
      )}

      <button onClick={onClose}>Close</button>
    </div>
  );
};

export default CalendarModal;
