    import React, { useState, useEffect } from 'react';

    const DiaryModal = ({ onAdd, onEdit, onDelete, onClose, selectedEmoji, selectedDate, selectedText, selectedIndex }) => {
      const [diaryText, setDiaryText] = useState('');
    
      useEffect(() => {
        setDiaryText(selectedText);
      }, [selectedText]);
    
      const handleSubmit = (event) => {
        event.preventDefault();
        if (selectedIndex === null) {
          onAdd(diaryText, selectedDate);
        } else {
          onEdit(diaryText, selectedDate, selectedIndex);
        }
        onClose();
      };
    
      return (
        <div>
          <p>Selected Emoji: {selectedEmoji}</p>
          <p>Date: {selectedDate}</p>
          <form onSubmit={handleSubmit}>
            <textarea
              value={diaryText}
              onChange={e => setDiaryText(e.target.value)}
            />
            <button type="submit">Save</button>
          </form>
          {selectedIndex !== null && <button onClick={() => onDelete(selectedIndex)}>Delete</button>}
          <button onClick={onClose}>Close</button>
        </div>
      );
    };
    
    export default DiaryModal;
