    // DiaryModal.js
import React from 'react';

const DiaryModal = ({ onClose, selectedEmoji, selectedDate, selectedText }) => {
  const handleDelete = () => {
    onClose();
  };

  return (
    <div>
      <p>Selected Emoji: {selectedEmoji}</p>
      <p>Date: {selectedDate}</p>
      <p>{selectedText}</p>
      <button onClick={handleDelete}>Close</button>
    </div>
  );
};

export default DiaryModal;
